ALTER TABLE form_parts ADD COLUMN advice_en TEXT;
ALTER TABLE form_parts ADD COLUMN advice_de TEXT;


UPDATE form_parts SET advice_en = E'Improve memory: collect in groups; associate, put new words in context to memorise them better; use pictures, sounds, picture-sound combinations, actions, etc. to memorise new words. to memorise new expressions; structured work, repetition of older material.' WHERE step_label = 'A';
UPDATE form_parts SET advice_de = E'Erinnerungsvermögen verbessern: in Gruppen sammeln;\nassoziieren, neue Wörter in einen Zusammenhang bringen, um sie sich besser zu merken; Bilder, Klänge/Laute, Bild-Klang\nKombinationen, Handlungen usw. benutzen, um sich neue\nAusdrücke besser zu merken; strukturiertes Arbeiten, Wiederholung älterer Materialien.' WHERE step_label = 'A';

UPDATE form_parts SET advice_en = E'Use learning techniques: repeat; practise sounds and writing; use formulae and tables; combine familiar elements in new and different in new and different ways; use the foreign language in different authentic situations involving the four skills skills (listening, reading, speaking, writing); read quickly to grasp the main message of a text; use reference books; take notes; summarise; thinking deductively (starting from general rules); analyse expressions; analyse languages contrastively; be careful with literal translation and direct transfer from one language to another language to another; check understanding in succession.' WHERE step_label = 'B';
UPDATE form_parts SET advice_de = E'Lerntechniken benutzen: wiederholen; Laute und Schrift üben;\nFormeln und Tabellen benutzen; vertraute Elemente auf neue und\nunterschiedliche Weise kombinieren; die Fremdsprache in\nverschiedenen authentischen Situationen benutzen, die die vier\nFertigkeiten einbeziehen (Hören, Lesen, Sprechen, Schreiben);\nrasches Lesen, um die Hauptaussage eines Textes zu erfassen;\nNachschlagewerke benutzen; Notizen machen; zusammenfassen;\ndeduktiv denken (von allgemeinen Regeln ausgehend); Ausdrücke\nanalysieren; Sprachen kontrastiv analysieren; vorsichtig sein bei\nwörtlicher Übersetzung und bei direkten Übertragungen von einer\nSprache zur anderen; das Verständnis in Folge neueren überprüfen.' WHERE step_label = 'B';

UPDATE form_parts SET advice_en = E'Compensate for lack of knowledge: Use clues to determine the meaning of foreign language expressions; understand global rather than understanding every detail; finding ways to understand the main spoken or written message despite limited language skills: e.g. use gestures, change to native language, use synonyms or paraphrases, invent new words.' WHERE step_label = 'C';
UPDATE form_parts SET advice_de = E'Fehlende Kenntnisse kompensieren: Indizien benutzen, um die\nBedeutung fremdsprachlicher Ausdrücke zu erraten; Global- statt\njedes Detail verstehen; Mittel finden, um die gesprochene oder\ngeschriebene Hauptaussage trotz der begrenzten Sprachkenntnisse\nzu verstehen: z. B. Gesten benutzen, zeitweilig in die Muttersprache\nwechseln, Synonyme oder Umschreibungen benutzen, neue Wörter\nerfinden.' WHERE step_label = 'C';

UPDATE form_parts SET advice_en = E'Organising and evaluating learning: having an overview of one''s own learning; use familiar learning materials in one''s own learning plan; decide whether to pay attention to details or to the whole; find out how your personal language learning works; organise your learning (timetable, environment, exercise book, etc.); set yourself goals; identify the goal of a task in the foreign language; plan a task in the foreign foreign language; finding opportunities to practise; noticing your own mistakes, recognise and learn from mistakes; evaluate your own progress.' WHERE step_label = 'D';
UPDATE form_parts SET advice_de = E'Das Lernen organisieren und evaluieren: einen Überblick über\ndas eigene Lernen haben; bekannte Lernmaterialien in den eigenen\nLernplan einsetzen; entscheiden, ob man auf Details oder aufs\nGanze achten will; herausfinden, wie das persönliche\nFremdsprachenlernen funktioniert; sich beim Lernen organisieren\n(Zeitplan, Umgebung, Heft, usw.); sich Ziele setzen; das Ziel einer\nAufgabe in der Fremdsprache identifizieren; eine Aufgabe in der\nFremdsprache planen; Gelegenheiten zum Üben finden; die eigenen\nFehler bemerken und daraus lernen; die eigenen Fortschritte\nevaluieren.' WHERE step_label = 'D';

UPDATE form_parts SET advice_en = E'Managing feelings and emotions: reducing anxiety; encouraging oneself through positive thinking; taking small risks; rewarding yourself; recognising your own tension; keeping a diary; talk to someone about your feelings and attitudes towards language learning.' WHERE step_label = 'E';
UPDATE form_parts SET advice_de = E'Gefühle und Emotionen managen: Angst verringern; sich durch\npositives Denken Mut machen; kleine Risiken eingehen; sich\nbelohnen; die eigene Anspannung wahrnehmen; ein Tagebuch\nführen; mit jemandem über die eigenen Gefühle und Einstellungen\ngegenüber dem Sprachenlernen sprechen.' WHERE step_label = 'E';

UPDATE form_parts SET advice_en = E'Learning from others: asking questions to get explanations; ask for improvement; co-operate with other competent speakers of the foreign language; develop (inter)cultural awareness; become aware of the thoughts and feelings of others.' WHERE step_label = 'F';
UPDATE form_parts SET advice_de = E'Von anderen lernen: Fragen stellen, um Erklärungen zu\nbekommen; um Verbesserung bitten; mit anderen Lernenden\nkooperieren; mit anderen kompetenten Sprechern der Fremdsprache\nkooperieren; ein (inter)kulturelles Bewusstsein entwickeln; sich\nGedanken und Gefühle anderer bewusst werden.' WHERE step_label = 'F';

ALTER TABLE form_parts ADD COLUMN advice_threshold NUMERIC(5, 2) DEFAULT 3;
